mapper.select:
select
from Base_Items bt,Base_ItemsDetail btd where bt.ID = btd.ItemsID and btd.DeleteMark = '0'
and bt.EnCode = #{baseItemsEnCode}

select
from Base_Items bt,Base_ItemsDetail btd where bt.ID = btd.ItemsID and btd.DeleteMark = '0' and bt.ID IN
#{id}

update.foreach:
update Base_ItemsDetail
ItemsID = #{item.itemsId,jdbcType=VARCHAR}, EnCode = #{item.enCode,jdbcType=VARCHAR}, ItemName = #{item.itemName,jdbcType=VARCHAR}, IsDefault = #{item.isDefault,jdbcType=BIT}, DeleteMark = #{item.deleteMark,jdbcType=BIT}, EnabledMark = #{item.enabledMark,jdbcType=BIT}, AddTime = #{item.addTime,jdbcType=TIMESTAMP}, AddUserID = #{item.addUserId,jdbcType=VARCHAR}, AddUserName = #{item.addUserName,jdbcType=VARCHAR}, ModifyTime = #{item.modifyTime,jdbcType=TIMESTAMP}, ModifyUserID = #{item.modifyUserId,jdbcType=VARCHAR}, ModifyName = #{item.modifyName,jdbcType=VARCHAR}, DeleteName = #{item.deleteName,jdbcType=VARCHAR}, DeleteTime = #{item.deleteTime,jdbcType=TIMESTAMP}, Remark = #{item.remark,jdbcType=VARCHAR}, SortCode = #{item.sortCode,jdbcType=INTEGER}
where id = #{item.id,jdbcType=VARCHAR}
